// src/components/PreviousChallenges.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Challenge from './Challenge'; // Import Challenge logic

function PreviousChallenges() {
  const [challenges, setChallenges] = useState([]);
  const [searchNumber, setSearchNumber] = useState('');

  useEffect(() => {
    const generatedChallenges = generateChallengesList();
    setChallenges(generatedChallenges);
  }, []);

  function generateChallengesList() {
    const challengesList = [];
    const startDate = new Date('2024-09-01T00:00:00Z');
    const today = new Date();
    const estOffset = -5.0;
    const estDate = new Date(today.getTime() + (estOffset * 60 * 60 * 1000));
    const yesterday = new Date(estDate.getTime() - (24 * 60 * 60 * 1000));
    const daysDiff = Math.floor((yesterday - startDate) / (1000 * 60 * 60 * 24)) + 1;

    for (let i = daysDiff; i >= 1; i--) {
      const date = new Date(startDate.getTime() + ((i - 1) * 24 * 60 * 60 * 1000));
      const formattedDate = date.toLocaleDateString(undefined, { year: 'numeric', month: 'long', day: 'numeric' });
      challengesList.push({
        number: i,
        date: formattedDate,
      });
    }
    return challengesList;
  }

function showAnswer(challengeNumber) {
  const challenge = generateChallengeForNumber(challengeNumber);
  alert(`Answer for challenge No. ${challengeNumber} is ${challenge.answer}`);
}

function generateChallengeForNumber(challengeNumber) {
  // Use the same logic as in Challenge.js to generate the challenge
  const seed = calculateSeedFromNumber(challengeNumber);
  const countryData = countries; // Ensure countries data is available
  // Generate the challenge using the same method as in Challenge.js
  return generateChallenge(countryData, seed);
}

