// src/components/Challenge.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Challenge({ challengeNumberProp }) {
  const { number } = useParams();
  const challengeNumber = challengeNumberProp || Number(number);
  const [countries, setCountries] = useState([]);
  const [currentChallenge, setCurrentChallenge] = useState(null);
  const [inputValues, setInputValues] = useState([]);
  const [triesLeft, setTriesLeft] = useState(5);
  const [message, setMessage] = useState('');
  const [gameEnded, setGameEnded] = useState(false);
  const [difficulty, setDifficulty] = useState('Easy');
  const inputRefs = useRef([]);
  const [guessHistory, setGuessHistory] = useState([]);
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    // Load city data
    setLocations(cities);
    initializeGame(cities);
  }, [challengeNumber]);

  useEffect(() => {
    // Fetch country data
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data);
        initializeGame(response.data);
      })
      .catch(error => {
        console.error('Error fetching country data:', error);
      });
  }, [challengeNumber]);

  function initializeGame(countryData) {
    const seed = calculateSeedFromNumber(challengeNumber);
    const challenge = generateChallenge(countryData, seed);
    setCurrentChallenge(challenge);
    setInputValues(Array(challenge.answer.length).fill(''));
    setTriesLeft(5);
    setMessage('');
    setGameEnded(false);
    setDifficulty(challenge.level);
    setGuessHistory([]);
    inputRefs.current = [];
  }

  function calculateSeedFromNumber(challengeNumber) {
    const startDate = new Date('2022-01-01T00:00:00Z');
    const seedDate = new Date(startDate.getTime() + ((challengeNumber - 1) * 24 * 60 * 60 * 1000));
    const seed = seedDate.getFullYear() * 10000 + (seedDate.getMonth() + 1) * 100 + seedDate.getDate();
    return seed;
  }

  function generateChallenge(countryData, seed) {
    // Use seed to generate deterministic challenge
    const levels = {
      'Easy': locationData.filter(city => city.population > 5000000),
      'Medium': locationData.filter(city => city.population <= 5000000 && city.population > 1000000),
      'Hard': locationData.filter(city => city.population <= 1000000),
    };

    const levelKeys = Object.keys(levels);
    const levelIndex = seed % levelKeys.length;
    const level = levelKeys[levelIndex];
    const countriesInLevel = levels[level];

    const countryIndex = seed % countriesInLevel.length;
    let country = countriesInLevel[countryIndex];

    while (!country.capital || !country.borders || !country.languages || !country.latlng) {
      country = countriesInLevel[(countryIndex + 1) % countriesInLevel.length];
    }

    const clues = generateClues(country);

    return {
      level: level,
      answer: country.name.common.toUpperCase(),
      country: country,
      clues: clues,
      fact: `The capital of ${country.name.common} is ${country.capital[0]}.`,
    };
  }

  function generateClues(location) {
    const clues = [];

    // Clue 1: Either longitude or latitude
    if (Math.random() < 0.5) {
      // Latitude clue
      const lat = location.latlng[0];
      const latDirection = lat >= 0 ? 'north' : 'south';
      clues.push(`Sail to the ${Math.abs(lat).toFixed(1)}¬∞ ${latDirection}.`);
    } else {
      // Longitude clue
      const lng = location.latlng[1];
      const lngDirection = lng >= 0 ? 'east' : 'west';
      clues.push(`Head towards ${Math.abs(lng).toFixed(1)}¬∞ ${lngDirection}.`);
    }

    // Clue 2: Compass clue
    const compassDirections = ['north', 'east', 'south', 'west'];
    const randomDirection = compassDirections[Math.floor(Math.random() * compassDirections.length)];
    clues.push(`Your treasure lies to the ${randomDirection}.`);

    // Clue 3: Geographical feature clue
    const features = [
      'a great river',
      'towering mountains',
      'a vast desert',
      'a deep lake',
      'a dense forest',
      'a sprawling plain',
      'a mysterious swamp',
      'a rugged coastline',
    ];

    const randomFeature = features[Math.floor(Math.random() * features.length)];
    clues.push(`You'll traverse ${randomFeature} on your journey.`);
    // Clue 4: Flag colors
    const colors = getFlagColors(location.flagUrl);
    if (colors.length > 0) {
      clues.push(`Hoist the flag with colors of ${colors.join(', ')}.`);
    }

    // Clue 5: Language origin or script
    const languageClue = generateLanguageClue(location);
    if (languageClue) {
      clues.push(languageClue);
    }

    return clues;
  }  

  function generateCompassClue(country) {
    const region = country.region;
    const subregion = country.subregion;

    let clue = 'Your treasure lies';
    if (region && subregion) {
      clue += ` in the lands of ${subregion}, within the greater ${region}.`;
    } else if (region) {
      clue += ` somewhere in ${region}.`;
    } else {
      clue += ' in a faraway land.';
    }

    return clue;
  }

  function generateLanguageClue(country) {
    const languageCodes = country.languages ? Object.keys(country.languages) : [];
    if (languageCodes.length === 0) return null;

    // Map of language codes to families or scripts
    const languageFamilies = {
      'ara': 'Semitic',
      'zho': 'Sino-Tibetan',
      'hin': 'Indo-Aryan',
      'ben': 'Indo-Aryan',
      'rus': 'Slavic',
      'jpn': 'Japonic',
      'deu': 'Germanic',
      'fra': 'Romance',
      'spa': 'Romance',
      'eng': 'Germanic',
      'por': 'Romance',
      'ita': 'Romance',
      'kor': 'Koreanic',
      'tur': 'Turkic',
      'vie': 'Austroasiatic',
      'tam': 'Dravidian',
      'urd': 'Indo-Aryan',
      'grn': 'Tupian',
      'amh': 'Afro-Asiatic',
      // Add more language code mappings as needed
    };

    const scripts = {
      'Arab': 'abjad script',
      'Cyrl': 'Cyrillic script',
      'Hans': 'simplified Chinese characters',
      'Hant': 'traditional Chinese characters',
      'Latn': 'Latin script',
      'Deva': 'Devanagari script',
      'Beng': 'Bengali script',
      'Jpan': 'Japanese script',
      'Kore': 'Hangul script',
      // Add more script mappings as needed
    };

    // Get the first language code
    const langCode = languageCodes[0];
    const family = languageFamilies[langCode];
    const script = scripts[country.languages[langCode]];

    let clue = 'The local tongue ';
    if (family) {
      clue += `traces back to the ${family} language family`;
    } else {
      clue += 'has ancient roots';
    }

    if (script) {
      clue += `, written in the ${script}`;
    }

    clue += '.';

    return clue;
  }

  function getFlagColors(flagUrl) {
    // Placeholder colors
    return ['red', 'white', 'blue'];
  }

  function handleInputChange(e, index) {
    const value = e.target.value.toUpperCase();
    if (/^[A-Z]?$/.test(value)) {
      const newInputValues = [...inputValues];
      newInputValues[index] = value;
      setInputValues(newInputValues);

      // Move to the next input if available
      if (value && index < inputRefs.current.length - 1) {
        inputRefs.current[index + 1].focus();
      }
    }
  }

  function handleKeyDown(e, index) {
    if (e.key === 'Backspace') {
      if (inputValues[index]) {
        const newInputValues = [...inputValues];
        newInputValues[index] = '';
        setInputValues(newInputValues);
      } else if (index > 0) {
        inputRefs.current[index - 1].focus();
      }
    }
  }

  function handleSubmit() {
    const userAnswer = inputValues.join('');
    const newGuessHistory = [...guessHistory, userAnswer];
    setGuessHistory(newGuessHistory);

    if (userAnswer === currentChallenge.answer) {
      setMessage(`${currentChallenge.fact}`);
      setGameEnded(true);
    } else {
      const newTriesLeft = triesLeft - 1;
      setTriesLeft(newTriesLeft);

      const directionHint = getDirectionHint(userAnswer);
      if (newTriesLeft > 0) {
        setMessage(`Incorrect. ${directionHint} Tries left: ${newTriesLeft}`);
      } else {
        setMessage(`Out of tries! The answer was ${currentChallenge.answer}.`);
        setGameEnded(true);
      }
    }
  }

  function getDirectionHint(userAnswer) {
    const guessedLocation = locations.find(
      (location) => location.name.toUpperCase() === userAnswer.toUpperCase()
    );

    if (!guessedLocation || !currentChallenge.location) {
      return 'That place is unknown to our maps.';
    }

    const guessedLatLng = guessedLocation.latlng;
    const actualLatLng = currentChallenge.location.latlng;

    const latDifference = actualLatLng[0] - guessedLatLng[0];
    const lngDifference = actualLatLng[1] - guessedLatLng[1];

    const latDirection = latDifference > 0 ? 'north' : 'south';
    const lngDirection = lngDifference > 0 ? 'east' : 'west';

    return `You're off course! Try heading ${Math.abs(latDifference).toFixed(1)}¬∞ ${latDirection} and ${Math.abs(lngDifference).toFixed(1)}¬∞ ${lngDirection}.`;
  }

  function copyResult() {
    let result = `TREZZLE N.${challengeNumber} ${guessHistory.length}/5\n\n`;
    guessHistory.forEach((guess, index) => {
      const isCorrect = (guess === currentChallenge.answer);
      const status = isCorrect ? 'üü©' : 'üü•';
      const letters = guess.split('').map(letter => `[${letter}]`).join(' ');
      result += `${index + 1}. ${letters} ${status}\n`;
    });
    navigator.clipboard.writeText(result);
    alert('Result copied to clipboard!');
  }

  function shareChallenge() {
    const url = window.location.href;
    navigator.clipboard.writeText(url);
    alert('Challenge URL copied to clipboard!');
  }

  if (!currentChallenge) {
    return <div>Loading...</div>;
  }


  return (
    <div className="today-challenge">
      <h2>{`Challenge No. ${challengeNumber}`}</h2>
      <h4>{currentChallenge.date}</h4>
      <div className="difficulty">Difficulty Level: <strong>{difficulty}</strong></div>
      <div className="clues">
        <h3>üó∫Ô∏è Clues:</h3>
        <ul>
          {currentChallenge.clues.map((clue, index) => (
            <li key={index}>{clue}</li>
          ))}
        </ul>
      </div>
      <div className="answer-boxes">
        {inputValues.map((value, index) => (
          <input
            key={index}
            type="text"
            maxLength="1"
            value={value}
            onChange={(e) => handleInputChange(e, index)}
            onKeyDown={(e) => handleKeyDown(e, index)}
            disabled={gameEnded}
            ref={(el) => (inputRefs.current[index] = el)}
          />
        ))}
      </div>
      <button className="btn btn-primary" onClick={handleSubmit} disabled={gameEnded}>Submit Answer</button>
      <div className="tries">
        {Array(triesLeft).fill('üü©').map((box, index) => (
          <span key={index}>{box}</span>
        ))}
      </div>
      <div className="message">
        {message && (
          <div className="speech-bubble">
            üê¶‚Äçüî• {message}
          </div>
        )}
      </div>
      {gameEnded && (
        <div>
          <p>Try the next challenge tomorrow at midnight EST!</p>
          <button className="btn btn-secondary" onClick={copyResult}>Share Your Result</button>
          <button className="btn btn-secondary ml-2" onClick={shareChallenge}>Share This Challenge</button>
        </div>
      )}
    </div>
  );
}

export default Challenge;
