import React from 'react';

function HowToPlay() {
  return (
    <div className="how-to-play">
      <h2>How To Play</h2>
      <ul>
        <li>Find the hidden country using the provided clues.</li>
        <li>Use a map to help solve the treasure hunt.</li>
        <li>You have 5 tries to guess the correct country.</li>
        <li>Enter your guess by typing the country's name.</li>
        <li>After each guess, you'll receive directional hints.</li>
      </ul>
      <p>A new puzzle is released daily at midnight.</p>
    </div>
  );
}

export default HowToPlay;
