// src/components/PreviousChallenges.js

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Papa from 'papaparse';

function PreviousChallenges() {
  const [challenges, setChallenges] = useState([]);
  const [searchNumber, setSearchNumber] = useState('');
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    // Fetch and parse the CSV file
    Papa.parse('/worldcities.csv', {
      download: true,
      header: true,
      complete: function(results) {
        const cities = results.data;
        const validCities = cities.filter(city => city.city_ascii && city.lat && city.lng && city.population);
        setLocations(validCities);
        const generatedChallenges = generateChallengesList(validCities);
        setChallenges(generatedChallenges);
      },
    });
  }, []);

  function generateChallengesList(locationData) {
    const challengesList = [];
    const startDate = new Date('2022-01-01T00:00:00Z');
    const today = new Date();
    const estOffset = -5.0;
    const estDate = new Date(today.getTime() + (estOffset * 60 * 60 * 1000));
    const yesterday = new Date(estDate.getTime() - (24 * 60 * 60 * 1000));
    const daysDiff = Math.floor((yesterday - startDate) / (1000 * 60 * 60 * 24)) + 1;

    for (let i = daysDiff; i >= 1; i--) {
      const date = new Date(startDate.getTime() + ((i - 1) * 24 * 60 * 60 * 1000));
      const formattedDate = date.toLocaleDateString(undefined, { year: 'numeric', month: 'long', day: 'numeric' });
      challengesList.push({
        number: i,
        date: formattedDate,
      });
    }
    return challengesList;
  }

  function showAnswer(challengeNumber) {
    const seed = calculateSeedFromNumber(challengeNumber);
    const challenge = generateChallenge(locations, seed);
    alert(`Answer for challenge No. ${challengeNumber} is ${challenge.answer}`);
  }

  function calculateSeedFromNumber(challengeNumber) {
    const startDate = new Date('2022-01-01T00:00:00Z');
    const seedDate = new Date(startDate.getTime() + ((challengeNumber - 1) * 24 * 60 * 60 * 1000));
    const seed = seedDate.getFullYear() * 10000 + (seedDate.getMonth() + 1) * 100 + seedDate.getDate();
    return seed;
  }

  function generateChallenge(locationData, seed) {
    // Same as in Challenge.js
    // ... copy the generateChallenge function here
  }

  const filteredChallenges = challenges.filter(challenge =>
    challenge.number.toString().includes(searchNumber)
  );

  return (
    <div className="container">
      <h2>Previous Challenges</h2>
      <input
        type="number"
        placeholder="Search by Number"
        value={searchNumber}
        onChange={(e) => setSearchNumber(e.target.value)}
        className="form-control mb-3"
      />
      <ul className="list-group">
        {filteredChallenges.map((challenge) => (
          <li key={challenge.number} className="list-group-item mb-2">
            <div className="row">
              <div className="col-md-8">
                {challenge.date} â€“ No. {challenge.number}
              </div>
              <div className="col-md-4 text-right">
                <Link to={`/challenge/${challenge.number}`} className="btn btn-primary btn-sm ml-2">
                  Take this Challenge
                </Link>
                <button className="btn btn-secondary btn-sm ml-2" onClick={() => showAnswer(challenge.number)}>
                  Show Answer
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PreviousChallenges;
